import React, { useState, useEffect } from 'react';
import { expenseAPI } from '../services/api';
import ExpenseForm from '../components/ExpenseForm';
import ExpenseStats from '../components/ExpenseStats';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [editingExpense, setEditingExpense] = useState(null);
  const [filters, setFilters] = useState({
    category: 'All',
    startDate: '',
    endDate: ''
  });

  // Fetch expenses
  const fetchExpenses = async () => {
    try {
      setLoading(true);
      const filterParams = {};
      
      if (filters.category !== 'All') {
        filterParams.category = filters.category;
      }
      if (filters.startDate) {
        filterParams.startDate = filters.startDate;
      }
      if (filters.endDate) {
        filterParams.endDate = filters.endDate;
      }

      const response = await expenseAPI.getExpenses(filterParams);
      setExpenses(response.data.expenses);
      setError(null);
    } catch (err) {
      setError('Failed to fetch expenses');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch stats
  const fetchStats = async () => {
    try {
      const response = await expenseAPI.getStats();
      setStats(response.data);
    } catch (err) {
      console.error('Failed to fetch stats:', err);
    }
  };

  // Load data on component mount and filter changes
  useEffect(() => {
    fetchExpenses();
    fetchStats();
  }, [filters]);

  // Handle expense creation/update
  const handleExpenseSubmit = async (expenseData) => {
    try {
      if (editingExpense) {
        await expenseAPI.updateExpense(editingExpense._id, expenseData);
      } else {
        await expenseAPI.createExpense(expenseData);
      }
      
      setShowForm(false);
      setEditingExpense(null);
      fetchExpenses();
      fetchStats();
    } catch (err) {
      console.error('Failed to save expense:', err);
      throw err;
    }
  };

  // Handle expense deletion
  const handleDeleteExpense = async (id) => {
    if (window.confirm('Are you sure you want to delete this expense?')) {
      try {
        await expenseAPI.deleteExpense(id);
        fetchExpenses();
        fetchStats();
      } catch (err) {
        console.error('Failed to delete expense:', err);
        alert('Failed to delete expense');
      }
    }
  };

  // Handle expense editing
  const handleEditExpense = (expense) => {
    setEditingExpense(expense);
    setShowForm(true);
  };

  // Handle viewing expense details
  const handleViewExpense = (expense) => {
    navigate(`/expense/${expense._id}`, { state: { expense } });
  };

  // Handle filter changes
  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  // Get category emoji
  const getCategoryEmoji = (category) => {
    const emojis = {
      'Food': 'üçî',
      'Transportation': 'üöó',
      'Entertainment': 'üé¨',
      'Healthcare': 'üè•',
      'Shopping': 'üõçÔ∏è',
      'Bills': 'üí°',
      'Education': 'üìö',
      'Travel': '‚úàÔ∏è',
      'Other': 'üìù'
    };
    return emojis[category] || 'üìù';
  };

  // Format date
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  if (loading && expenses.length === 0) {
    return (
      <div className="page-container">
        <div className="container">
          <div className="text-center" style={{ marginTop: '100px' }}>
            <div className="spinner" style={{ margin: '0 auto' }}></div>
            <p className="text-secondary mt-md">Loading your expenses...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="page-container">
      <div className="container slide-up">
        {/* Header */}
        <div className="flex justify-between items-center mb-lg">
          <div>
            <h1 className="text-4xl font-bold text-primary mb-sm">
              üí∞ Expense Dashboard
            </h1>
            <p className="text-secondary">
              Manage and track your expenses with ease
            </p>
          </div>
          <button 
            className="btn btn-primary btn-lg"
            onClick={() => {
              setEditingExpense(null);
              setShowForm(true);
            }}
          >
            ‚ûï Add New Expense
          </button>
        </div>

        {/* Error Message */}
        {error && (
          <div className="message message-error">
            ‚ö†Ô∏è {error}
          </div>
        )}

        {/* Stats Section */}
        {stats && <ExpenseStats stats={stats} />}

        {/* Filters */}
        <div className="filters">
          <div className="card-header">
            <h3 className="card-title">Filter Expenses</h3>
          </div>
          <div className="filters-grid">
            <div className="form-group">
              <label className="form-label">Category</label>
              <select
                className="form-select"
                value={filters.category}
                onChange={(e) => handleFilterChange({ ...filters, category: e.target.value })}
              >
                <option value="All">All Categories</option>
                <option value="Food">üçî Food</option>
                <option value="Transportation">üöó Transportation</option>
                <option value="Entertainment">üé¨ Entertainment</option>
                <option value="Healthcare">üè• Healthcare</option>
                <option value="Shopping">üõçÔ∏è Shopping</option>
                <option value="Bills">üí° Bills</option>
                <option value="Education">üìö Education</option>
                <option value="Travel">‚úàÔ∏è Travel</option>
                <option value="Other">üìù Other</option>
              </select>
            </div>
            
            <div className="form-group">
              <label className="form-label">Start Date</label>
              <input
                className="form-input"
                type="date"
                value={filters.startDate}
                onChange={(e) => handleFilterChange({ ...filters, startDate: e.target.value })}
              />
            </div>
            
            <div className="form-group">
              <label className="form-label">End Date</label>
              <input
                className="form-input"
                type="date"
                value={filters.endDate}
                onChange={(e) => handleFilterChange({ ...filters, endDate: e.target.value })}
              />
            </div>
            
            <button 
              className="btn btn-outline"
              onClick={() => setFilters({ category: 'All', startDate: '', endDate: '' })}
            >
              Clear Filters
            </button>
          </div>
        </div>

        {/* Expense Form Modal */}
        {showForm && (
          <div className="modal-backdrop fade-in">
            <div className="modal slide-up">
              <ExpenseForm
                expense={editingExpense}
                onSubmit={handleExpenseSubmit}
                onCancel={() => {
                  setShowForm(false);
                  setEditingExpense(null);
                }}
              />
            </div>
          </div>
        )}

        {/* Expenses Grid */}
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Recent Expenses</h3>
            <p className="card-subtitle">
              {expenses.length} expense{expenses.length !== 1 ? 's' : ''} found
            </p>
          </div>

          {loading ? (
            <div className="text-center">
              <div className="spinner" style={{ margin: '0 auto' }}></div>
              <p className="text-secondary mt-md">Loading expenses...</p>
            </div>
          ) : expenses.length === 0 ? (
            <div className="text-center">
              <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üìä</div>
              <h3 className="text-secondary mb-sm">No expenses found</h3>
              <p className="text-muted">
                {filters.category !== 'All' || filters.startDate || filters.endDate
                  ? 'Try adjusting your filters to see more expenses.'
                  : 'Get started by adding your first expense.'}
              </p>
              <button 
                className="btn btn-primary mt-md"
                onClick={() => {
                  setEditingExpense(null);
                  setShowForm(true);
                }}
              >
                Add Your First Expense
              </button>
            </div>
          ) : (
            <div className="expenses-grid">
              {expenses.map((expense) => (
                <div 
                  key={expense._id} 
                  className={`expense-card category-${expense.category.toLowerCase()}`}
                  onClick={() => handleViewExpense(expense)}
                >
                  <div className="expense-card-header">
                    <h4 className="expense-card-title">{expense.title}</h4>
                    <div className="expense-card-amount">
                      {formatCurrency(expense.amount)}
                    </div>
                  </div>
                  
                  <div className="expense-card-meta">
                    <div className="expense-card-category">
                      {getCategoryEmoji(expense.category)}
                      {expense.category}
                    </div>
                    <div className="expense-card-date">
                      {formatDate(expense.date)}
                    </div>
                  </div>
                  
                  {expense.description && (
                    <div className="expense-card-description">
                      {expense.description}
                    </div>
                  )}
                  
                  <div className="expense-card-actions">
                    <button 
                      className="btn btn-sm btn-ghost"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleViewExpense(expense);
                      }}
                    >
                      üëÅÔ∏è View
                    </button>
                    <button 
                      className="btn btn-sm btn-outline"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleEditExpense(expense);
                      }}
                    >
                      ‚úèÔ∏è Edit
                    </button>
                    <button 
                      className="btn btn-sm btn-danger"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteExpense(expense._id);
                      }}
                    >
                      üóëÔ∏è Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
