import React from 'react';

const ExpenseStats = ({ stats }) => {
  const formatAmount = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getCategoryColor = (category) => {
    const colors = {
      Food: '#ff6b6b',
      Transportation: '#4ecdc4',
      Entertainment: '#45b7d1',
      Healthcare: '#f39c12',
      Shopping: '#e74c3c',
      Bills: '#9b59b6',
      Education: '#2ecc71',
      Travel: '#1abc9c',
      Other: '#95a5a6'
    };
    return colors[category] || '#95a5a6';
  };

  return (
    <div style={{ display: 'grid', gap: '25px', marginBottom: '35px' }}>
      {/* Summary Cards */}
      <div className="stats-grid">
        <div className="stats-card" style={{ background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%)' }}>
          <h4 style={{ margin: '0 0 15px 0', color: 'white', fontSize: '14px', textTransform: 'uppercase', letterSpacing: '1px' }}>üìä Total Expenses</h4>
          <div className="stats-value">
            {stats.totalExpenses}
          </div>
        </div>
        
        <div className="stats-card" style={{ background: 'linear-gradient(135deg, rgba(255, 107, 107, 0.1) 0%, rgba(238, 90, 82, 0.1) 100%)' }}>
          <h4 style={{ margin: '0 0 15px 0', color: 'white', fontSize: '14px', textTransform: 'uppercase', letterSpacing: '1px' }}>üí≥ Total Amount</h4>
          <div className="stats-value" style={{ color: '#ff6b6b' }}>
            {formatAmount(stats.totalAmount)}
          </div>
        </div>
        
        <div className="stats-card" style={{ background: 'linear-gradient(135deg, rgba(81, 207, 102, 0.1) 0%, rgba(64, 192, 87, 0.1) 100%)' }}>
          <h4 style={{ margin: '0 0 15px 0', color: 'white', fontSize: '14px', textTransform: 'uppercase', letterSpacing: '1px' }}>üìà Average per Expense</h4>
          <div className="stats-value" style={{ color: '#51cf66' }}>
            {formatAmount(stats.totalExpenses > 0 ? stats.totalAmount / stats.totalExpenses : 0)}
          </div>
        </div>
      </div>

      {/* Category Breakdown */}
      {stats.categoryStats && stats.categoryStats.length > 0 && (
        <div className="card">
          <h3 style={{ marginBottom: '25px', color: '#333', fontWeight: '700', fontSize: '20px' }}>
            üìä Spending by Category
          </h3>
          <div style={{ display: 'grid', gap: '15px' }}>
            {stats.categoryStats.map((category, index) => {
              const percentage = ((category.totalAmount / stats.totalAmount) * 100).toFixed(1);
              const categoryEmoji = {
                Food: 'üçî',
                Transportation: 'üöó',
                Entertainment: 'üé¨',
                Healthcare: 'üè•',
                Shopping: 'üõçÔ∏è',
                Bills: 'üí°',
                Education: 'üìö',
                Travel: '‚úàÔ∏è',
                Other: 'üìù'
              };
              
              return (
                <div key={index} style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'space-between',
                  padding: '18px 20px',
                  background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.1) 0%, rgba(255, 255, 255, 0.05) 100%)',
                  borderRadius: '12px',
                  border: `2px solid ${getCategoryColor(category._id)}`,
                  transition: 'all 0.3s ease',
                  cursor: 'pointer'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateX(5px)';
                  e.currentTarget.style.boxShadow = `0 5px 15px ${getCategoryColor(category._id)}33`;
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateX(0)';
                  e.currentTarget.style.boxShadow = 'none';
                }}
                >
                  <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
                    <div style={{ fontSize: '24px' }}>
                      {categoryEmoji[category._id] || 'üìù'}
                    </div>
                    <div>
                      <span style={{ fontWeight: '600', fontSize: '16px', color: '#333' }}>
                        {category._id}
                      </span>
                      <div style={{ color: '#666', fontSize: '13px', marginTop: '2px' }}>
                        {category.count} expense{category.count !== 1 ? 's' : ''}
                      </div>
                    </div>
                  </div>
                  
                  <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
                    <div style={{ 
                      background: `linear-gradient(90deg, ${getCategoryColor(category._id)} 0%, ${getCategoryColor(category._id)}88 100%)`,
                      height: '8px',
                      borderRadius: '4px',
                      width: `${Math.max(percentage * 3, 30)}px`,
                      boxShadow: `0 2px 8px ${getCategoryColor(category._id)}33`
                    }} />
                    <div style={{ textAlign: 'right' }}>
                      <div style={{ fontWeight: '700', fontSize: '16px', color: getCategoryColor(category._id) }}>
                        {formatAmount(category.totalAmount)}
                      </div>
                      <div style={{ color: '#666', fontSize: '12px', fontWeight: '500' }}>
                        {percentage}%
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default ExpenseStats;
