import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [formErrors, setFormErrors] = useState({});

  const { register, loading, error, isAuthenticated, clearError } = useAuth();
  const navigate = useNavigate();

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/dashboard');
    }
  }, [isAuthenticated, navigate]);

  // Clear errors when component mounts
  useEffect(() => {
    clearError();
  }, [clearError]);

  const { name, email, password, confirmPassword } = formData;

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    // Clear field error when user starts typing
    if (formErrors[e.target.name]) {
      setFormErrors({
        ...formErrors,
        [e.target.name]: ''
      });
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!name.trim()) {
      errors.name = 'Name is required';
    } else if (name.trim().length < 2) {
      errors.name = 'Name must be at least 2 characters';
    }

    if (!email.trim()) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      errors.email = 'Email is invalid';
    }

    if (!password) {
      errors.password = 'Password is required';
    } else if (password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    if (!confirmPassword) {
      errors.confirmPassword = 'Please confirm your password';
    } else if (password !== confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
    }

    return errors;
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    const result = await register({ name, email, password });
    if (result.success) {
      navigate('/dashboard');
    }
  };

  return (
    <div className="auth-container fade-in">
      <div className="auth-card slide-up">
        <div className="auth-header">
          <h1 className="auth-title">üéâ Join Us Today</h1>
          <p className="auth-subtitle">
            Create your account and start tracking expenses
          </p>
        </div>
        
        {error && (
          <div className="message message-error">
            ‚ö†Ô∏è {error}
          </div>
        )}
        
        <form onSubmit={onSubmit}>
          <div className="form-group">
            <label htmlFor="name" className="form-label">üë§ Full Name</label>
            <input
              type="text"
              id="name"
              name="name"
              className="form-input"
              value={name}
              onChange={onChange}
              placeholder="Enter your full name"
            />
            {formErrors.name && <div className="message message-error">{formErrors.name}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="email" className="form-label">üìß Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              className="form-input"
              value={email}
              onChange={onChange}
              placeholder="Enter your email address"
            />
            {formErrors.email && <div className="message message-error">{formErrors.email}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="password" className="form-label">üîí Password</label>
            <input
              type="password"
              id="password"
              name="password"
              className="form-input"
              value={password}
              onChange={onChange}
              placeholder="Create a password (min. 6 characters)"
            />
            {formErrors.password && <div className="message message-error">{formErrors.password}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword" className="form-label">üîê Confirm Password</label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              className="form-input"
              value={confirmPassword}
              onChange={onChange}
              placeholder="Confirm your password"
            />
            {formErrors.confirmPassword && <div className="message message-error">{formErrors.confirmPassword}</div>}
          </div>

          <button 
            type="submit" 
            className="btn btn-primary btn-lg" 
            style={{ width: '100%' }}
            disabled={loading}
          >
            {loading ? (
              <span className="flex items-center justify-center gap-sm">
                <div className="spinner"></div>
                Creating Account...
              </span>
            ) : (
              'üöÄ Create Account'
            )}
          </button>
        </form>

        <div className="text-center mt-lg">
          <p className="text-secondary">
            Already have an account? {' '}
            <Link 
              to="/login" 
              className="text-primary font-semibold"
              style={{ textDecoration: 'none' }}
            >
              Sign in here
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
