import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [formErrors, setFormErrors] = useState({});

  const { register, loading, error, isAuthenticated, clearError } = useAuth();
  const navigate = useNavigate();

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/dashboard');
    }
  }, [isAuthenticated, navigate]);

  // Clear errors when component mounts
  useEffect(() => {
    clearError();
  }, [clearError]);

  const { name, email, password, confirmPassword } = formData;

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    // Clear field error when user starts typing
    if (formErrors[e.target.name]) {
      setFormErrors({
        ...formErrors,
        [e.target.name]: ''
      });
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!name.trim()) {
      errors.name = 'Name is required';
    } else if (name.trim().length < 2) {
      errors.name = 'Name must be at least 2 characters';
    }

    if (!email.trim()) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      errors.email = 'Email is invalid';
    }

    if (!password) {
      errors.password = 'Password is required';
    } else if (password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    if (!confirmPassword) {
      errors.confirmPassword = 'Please confirm your password';
    } else if (password !== confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
    }

    return errors;
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    const result = await register({ name, email, password });
    if (result.success) {
      navigate('/dashboard');
    }
  };

  return (
    <div className="container">
      <form onSubmit={onSubmit} className="auth-form card">
        <h2>üéâ Join Us Today</h2>
        <p style={{ textAlign: 'center', color: '#666', marginBottom: '30px' }}>
          Create your account and start tracking expenses
        </p>
        
        {error && (
          <div style={{ 
            background: 'rgba(220, 53, 69, 0.1)', 
            border: '2px solid #dc3545', 
            borderRadius: '12px',
            padding: '15px',
            marginBottom: '20px',
            textAlign: 'center'
          }}>
            <div className="error" style={{ fontSize: '16px' }}>{error}</div>
          </div>
        )}
        
        <div className="form-group">
          <label htmlFor="name">üë§ Full Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={name}
            onChange={onChange}
            placeholder="Enter your full name"
          />
          {formErrors.name && <div className="error">{formErrors.name}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="email">üìß Email Address</label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onChange={onChange}
            placeholder="Enter your email address"
          />
          {formErrors.email && <div className="error">{formErrors.email}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="password">üîí Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={onChange}
            placeholder="Create a secure password"
          />
          {formErrors.password && <div className="error">{formErrors.password}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="confirmPassword">üîê Confirm Password</label>
          <input
            type="password"
            id="confirmPassword"
            name="confirmPassword"
            value={confirmPassword}
            onChange={onChange}
            placeholder="Confirm your password"
          />
          {formErrors.confirmPassword && <div className="error">{formErrors.confirmPassword}</div>}
        </div>

        <button 
          type="submit" 
          className="btn btn-primary" 
          style={{ width: '100%', fontSize: '16px', fontWeight: '600' }}
          disabled={loading}
        >
          {loading ? (
            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
              <div className="loading"></div>
              Creating Account...
            </span>
          ) : (
            'üéØ Create Account'
          )}
        </button>

        <p className="text-center mt-3" style={{ color: 'white' }}>
          Already have an account? {' '}
          <Link 
            to="/login" 
            style={{ 
              color: '#51cf66', 
              textDecoration: 'none', 
              fontWeight: '600',
              borderBottom: '2px solid transparent',
              transition: 'border-color 0.3s ease'
            }}
            onMouseEnter={(e) => e.target.style.borderBottomColor = '#51cf66'}
            onMouseLeave={(e) => e.target.style.borderBottomColor = 'transparent'}
          >
            Sign in here
          </Link>
        </p>
      </form>
    </div>
  );
};

export default Register;
