import React, { useState, useEffect } from 'react';

const ExpenseForm = ({ expense, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    title: '',
    amount: '',
    category: 'Food',
    description: '',
    date: new Date().toISOString().split('T')[0]
  });
  const [formErrors, setFormErrors] = useState({});
  const [loading, setLoading] = useState(false);

  // Populate form with expense data when editing
  useEffect(() => {
    if (expense) {
      setFormData({
        title: expense.title,
        amount: expense.amount.toString(),
        category: expense.category,
        description: expense.description || '',
        date: new Date(expense.date).toISOString().split('T')[0]
      });
    }
  }, [expense]);

  const { title, amount, category, description, date } = formData;

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    // Clear field error when user starts typing
    if (formErrors[e.target.name]) {
      setFormErrors({
        ...formErrors,
        [e.target.name]: ''
      });
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!title.trim()) {
      errors.title = 'Title is required';
    }

    if (!amount) {
      errors.amount = 'Amount is required';
    } else if (isNaN(amount) || parseFloat(amount) <= 0) {
      errors.amount = 'Amount must be a positive number';
    }

    if (!category) {
      errors.category = 'Category is required';
    }

    if (!date) {
      errors.date = 'Date is required';
    }

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    setLoading(true);

    try {
      await onSubmit({
        title: title.trim(),
        amount: parseFloat(amount),
        category,
        description: description.trim(),
        date
      });
    } catch (error) {
      console.error('Error submitting expense:', error);
      alert('Failed to save expense. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2 style={{ 
        textAlign: 'center', 
        marginBottom: '30px', 
        color: '#333',
        fontSize: '24px',
        fontWeight: '700'
      }}>
        {expense ? '‚úèÔ∏è Edit Expense' : '‚ûï Add New Expense'}
      </h2>
      
      <div className="form-group">
        <label htmlFor="title">üíº Title</label>
        <input
          type="text"
          id="title"
          name="title"
          value={title}
          onChange={onChange}
          placeholder="Enter expense title (e.g., Lunch at restaurant)"
        />
        {formErrors.title && <div className="error">{formErrors.title}</div>}
      </div>

      <div className="form-group">
        <label htmlFor="amount">üí∞ Amount</label>
        <input
          type="number"
          id="amount"
          name="amount"
          value={amount}
          onChange={onChange}
          placeholder="0.00"
          step="0.01"
          min="0"
        />
        {formErrors.amount && <div className="error">{formErrors.amount}</div>}
      </div>

      <div className="form-group">
        <label htmlFor="category">üìÇ Category</label>
        <select
          id="category"
          name="category"
          value={category}
          onChange={onChange}
        >
          <option value="Food">üçî Food</option>
          <option value="Transportation">üöó Transportation</option>
          <option value="Entertainment">üé¨ Entertainment</option>
          <option value="Healthcare">üè• Healthcare</option>
          <option value="Shopping">üõçÔ∏è Shopping</option>
          <option value="Bills">üí° Bills</option>
          <option value="Education">üìö Education</option>
          <option value="Travel">‚úàÔ∏è Travel</option>
          <option value="Other">üìù Other</option>
        </select>
        {formErrors.category && <div className="error">{formErrors.category}</div>}
      </div>

      <div className="form-group">
        <label htmlFor="description">üí¨ Description (Optional)</label>
        <textarea
          id="description"
          name="description"
          value={description}
          onChange={onChange}
          placeholder="Add any additional details about this expense..."
          rows="3"
        />
      </div>

      <div className="form-group">
        <label htmlFor="date">üìÖ Date</label>
        <input
          type="date"
          id="date"
          name="date"
          value={date}
          onChange={onChange}
        />
        {formErrors.date && <div className="error">{formErrors.date}</div>}
      </div>

      <div style={{ display: 'flex', gap: '15px', justifyContent: 'flex-end', marginTop: '30px' }}>
        <button 
          type="button" 
          className="btn btn-secondary"
          onClick={onCancel}
          disabled={loading}
        >
          ‚ùå Cancel
        </button>
        <button 
          type="submit" 
          className="btn btn-primary"
          disabled={loading}
          style={{ minWidth: '120px' }}
        >
          {loading ? (
            <span style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <div className="loading"></div>
              Saving...
            </span>
          ) : (
            `${expense ? 'üíæ Update' : '‚úÖ Add'} Expense`
          )}
        </button>
      </div>
    </form>
  );
};

export default ExpenseForm;
