import React from 'react';

const ExpenseStats = ({ stats }) => {
  const formatAmount = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getCategoryColor = (category) => {
    const colors = {
      Food: '#ff6b6b',
      Transportation: '#4ecdc4',
      Entertainment: '#45b7d1',
      Healthcare: '#f39c12',
      Shopping: '#e74c3c',
      Bills: '#9b59b6',
      Education: '#2ecc71',
      Travel: '#1abc9c',
      Other: '#95a5a6'
    };
    return colors[category] || '#95a5a6';
  };

  return (
    <div style={{ display: 'grid', gap: '25px', marginBottom: '35px' }}>
      {/* Summary Cards */}
      <div className="stats-grid">
        <div className="stats-card" style={{ background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%)' }}>
          <h4 style={{ margin: '0 0 15px 0', color: 'white', fontSize: '14px', textTransform: 'uppercase', letterSpacing: '1px' }}>ðŸ“Š Total Expenses</h4>
          <div className="stats-value">
            {stats.totalExpenses}
          </div>
        </div>
        
        <div className="stats-card" style={{ background: 'linear-gradient(135deg, rgba(255, 107, 107, 0.1) 0%, rgba(238, 90, 82, 0.1) 100%)' }}>
          <h4 style={{ margin: '0 0 15px 0', color: 'white', fontSize: '14px', textTransform: 'uppercase', letterSpacing: '1px' }}>ðŸ’³ Total Amount</h4>
          <div className="stats-value" style={{ color: '#ff6b6b' }}>
            {formatAmount(stats.totalAmount)}
          </div>
        </div>
        
        <div className="stats-card" style={{ background: 'linear-gradient(135deg, rgba(81, 207, 102, 0.1) 0%, rgba(64, 192, 87, 0.1) 100%)' }}>
          <h4 style={{ margin: '0 0 15px 0', color: 'white', fontSize: '14px', textTransform: 'uppercase', letterSpacing: '1px' }}>ðŸ“ˆ Average per Expense</h4>
          <div className="stats-value" style={{ color: '#51cf66' }}>
            {formatAmount(stats.totalExpenses > 0 ? stats.totalAmount / stats.totalExpenses : 0)}
          </div>
        </div>
      </div>

      {/* Category Breakdown */}
      {stats.categoryStats && stats.categoryStats.length > 0 && (
        <div className="card">
          <h3 style={{ marginBottom: '20px' }}>Spending by Category</h3>
          <div style={{ display: 'grid', gap: '10px' }}>
            {stats.categoryStats.map((category, index) => {
              const percentage = ((category.totalAmount / stats.totalAmount) * 100).toFixed(1);
              
              return (
                <div key={index} style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'space-between',
                  padding: '10px',
                  backgroundColor: '#f8f9fa',
                  borderRadius: '6px',
                  border: `3px solid ${getCategoryColor(category._id)}`
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                    <div 
                      style={{
                        width: '20px',
                        height: '20px',
                        backgroundColor: getCategoryColor(category._id),
                        borderRadius: '50%'
                      }}
                    />
                    <span style={{ fontWeight: 'bold' }}>{category._id}</span>
                    <span style={{ color: '#666', fontSize: '14px' }}>
                      ({category.count} expense{category.count !== 1 ? 's' : ''})
                    </span>
                  </div>
                  
                  <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
                    <div style={{ 
                      backgroundColor: getCategoryColor(category._id),
                      height: '8px',
                      borderRadius: '4px',
                      width: `${Math.max(percentage * 2, 20)}px`,
                      opacity: 0.7
                    }} />
                    <span style={{ fontWeight: 'bold', minWidth: '80px', textAlign: 'right' }}>
                      {formatAmount(category.totalAmount)}
                    </span>
                    <span style={{ color: '#666', minWidth: '50px', textAlign: 'right' }}>
                      {percentage}%
                    </span>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default ExpenseStats;
