import React, { useState, useEffect } from 'react';
import { expenseAPI } from '../services/api';
import ExpenseList from '../components/ExpenseList';
import ExpenseForm from '../components/ExpenseForm';
import ExpenseStats from '../components/ExpenseStats';

const Dashboard = () => {
  const [expenses, setExpenses] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [editingExpense, setEditingExpense] = useState(null);
  const [filters, setFilters] = useState({
    category: 'All',
    startDate: '',
    endDate: ''
  });

  // Fetch expenses
  const fetchExpenses = async () => {
    try {
      setLoading(true);
      const filterParams = {};
      
      if (filters.category !== 'All') {
        filterParams.category = filters.category;
      }
      if (filters.startDate) {
        filterParams.startDate = filters.startDate;
      }
      if (filters.endDate) {
        filterParams.endDate = filters.endDate;
      }

      const response = await expenseAPI.getExpenses(filterParams);
      setExpenses(response.data.expenses);
      setError(null);
    } catch (err) {
      setError('Failed to fetch expenses');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch stats
  const fetchStats = async () => {
    try {
      const response = await expenseAPI.getStats();
      setStats(response.data);
    } catch (err) {
      console.error('Failed to fetch stats:', err);
    }
  };

  // Load data on component mount and filter changes
  useEffect(() => {
    fetchExpenses();
    fetchStats();
  }, [filters]);

  // Handle expense creation/update
  const handleExpenseSubmit = async (expenseData) => {
    try {
      if (editingExpense) {
        await expenseAPI.updateExpense(editingExpense._id, expenseData);
      } else {
        await expenseAPI.createExpense(expenseData);
      }
      
      setShowForm(false);
      setEditingExpense(null);
      fetchExpenses();
      fetchStats();
    } catch (err) {
      console.error('Failed to save expense:', err);
      throw err;
    }
  };

  // Handle expense deletion
  const handleDeleteExpense = async (id) => {
    if (window.confirm('Are you sure you want to delete this expense?')) {
      try {
        await expenseAPI.deleteExpense(id);
        fetchExpenses();
        fetchStats();
      } catch (err) {
        console.error('Failed to delete expense:', err);
        alert('Failed to delete expense');
      }
    }
  };

  // Handle expense editing
  const handleEditExpense = (expense) => {
    setEditingExpense(expense);
    setShowForm(true);
  };

  // Handle filter changes
  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  if (loading && expenses.length === 0) {
    return (
      <div className="container text-center" style={{ marginTop: '100px' }}>
        <div>Loading...</div>
      </div>
    );
  }

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1 style={{ color: 'white', fontSize: '32px', fontWeight: '700', textShadow: '2px 2px 4px rgba(0,0,0,0.1)' }}>
          üí∞ Expense Dashboard
        </h1>
        <button 
          className="btn btn-primary"
          onClick={() => {
            setEditingExpense(null);
            setShowForm(true);
          }}
          style={{ fontSize: '16px', fontWeight: '600' }}
        >
          ‚ûï Add New Expense
        </button>
      </div>

      {error && (
        <div className="card" style={{ background: 'rgba(220, 53, 69, 0.1)', border: '2px solid #dc3545', marginBottom: '20px' }}>
          <div className="error" style={{ fontSize: '16px', textAlign: 'center' }}>{error}</div>
        </div>
      )}

      {/* Stats Section */}
      {stats && <ExpenseStats stats={stats} />}

      {/* Filters */}
      <div className="filter-section">
        <h3 style={{ color: 'white', marginBottom: '20px', fontWeight: '600' }}>Filter Expenses</h3>
        <div className="filter-grid">
          <div className="form-group" style={{ minWidth: '150px' }}>
            <label style={{ color: 'white' }}>Category</label>
            <select
              value={filters.category}
              onChange={(e) => handleFilterChange({ ...filters, category: e.target.value })}
            >
              <option value="All">All Categories</option>
              <option value="Food">üçî Food</option>
              <option value="Transportation">üöó Transportation</option>
              <option value="Entertainment">üé¨ Entertainment</option>
              <option value="Healthcare">üè• Healthcare</option>
              <option value="Shopping">üõçÔ∏è Shopping</option>
              <option value="Bills">üí° Bills</option>
              <option value="Education">üìö Education</option>
              <option value="Travel">‚úàÔ∏è Travel</option>
              <option value="Other">üìù Other</option>
            </select>
          </div>
          
          <div className="form-group" style={{ minWidth: '150px' }}>
            <label style={{ color: 'white' }}>Start Date</label>
            <input
              type="date"
              value={filters.startDate}
              onChange={(e) => handleFilterChange({ ...filters, startDate: e.target.value })}
            />
          </div>
          
          <div className="form-group" style={{ minWidth: '150px' }}>
            <label style={{ color: 'white' }}>End Date</label>
            <input
              type="date"
              value={filters.endDate}
              onChange={(e) => handleFilterChange({ ...filters, endDate: e.target.value })}
            />
          </div>
          
          <button 
            className="btn btn-secondary"
            onClick={() => setFilters({ category: 'All', startDate: '', endDate: '' })}
          >
            Clear Filters
          </button>
        </div>
      </div>

      {/* Expense Form Modal */}
      {showForm && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          zIndex: 1000
        }}>
          <div style={{
            backgroundColor: 'white',
            padding: '20px',
            borderRadius: '8px',
            width: '90%',
            maxWidth: '500px',
            maxHeight: '90vh',
            overflow: 'auto'
          }}>
            <ExpenseForm
              expense={editingExpense}
              onSubmit={handleExpenseSubmit}
              onCancel={() => {
                setShowForm(false);
                setEditingExpense(null);
              }}
            />
          </div>
        </div>
      )}

      {/* Expense List */}
      <ExpenseList
        expenses={expenses}
        loading={loading}
        onEdit={handleEditExpense}
        onDelete={handleDeleteExpense}
      />
    </div>
  );
};

export default Dashboard;
