import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { expenseAPI } from '../services/api';
import ExpenseForm from '../components/ExpenseForm';

const AllExpenses = () => {
  const navigate = useNavigate();
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [editingExpense, setEditingExpense] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    category: 'All',
    startDate: '',
    endDate: '',
    sortBy: 'date',
    sortOrder: 'desc'
  });

  // Fetch expenses
  const fetchExpenses = async () => {
    try {
      setLoading(true);
      const filterParams = {};
      
      if (filters.category !== 'All') {
        filterParams.category = filters.category;
      }
      if (filters.startDate) {
        filterParams.startDate = filters.startDate;
      }
      if (filters.endDate) {
        filterParams.endDate = filters.endDate;
      }

      const response = await expenseAPI.getExpenses(filterParams);
      let fetchedExpenses = response.data.expenses;

      // Apply search filter
      if (searchTerm) {
        fetchedExpenses = fetchedExpenses.filter(expense =>
          expense.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          expense.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
          expense.category.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }

      // Apply sorting
      fetchedExpenses.sort((a, b) => {
        let aValue, bValue;
        
        switch (filters.sortBy) {
          case 'amount':
            aValue = a.amount;
            bValue = b.amount;
            break;
          case 'title':
            aValue = a.title.toLowerCase();
            bValue = b.title.toLowerCase();
            break;
          case 'category':
            aValue = a.category.toLowerCase();
            bValue = b.category.toLowerCase();
            break;
          default:
            aValue = new Date(a.date);
            bValue = new Date(b.date);
        }

        if (filters.sortOrder === 'asc') {
          return aValue > bValue ? 1 : -1;
        } else {
          return aValue < bValue ? 1 : -1;
        }
      });

      setExpenses(fetchedExpenses);
      setError(null);
    } catch (err) {
      setError('Failed to fetch expenses');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExpenses();
  }, [filters, searchTerm]);

  // Handle expense creation/update
  const handleExpenseSubmit = async (expenseData) => {
    try {
      if (editingExpense) {
        await expenseAPI.updateExpense(editingExpense._id, expenseData);
      } else {
        await expenseAPI.createExpense(expenseData);
      }
      
      setShowForm(false);
      setEditingExpense(null);
      fetchExpenses();
    } catch (err) {
      console.error('Failed to save expense:', err);
      throw err;
    }
  };

  // Handle expense deletion
  const handleDeleteExpense = async (id) => {
    if (window.confirm('Are you sure you want to delete this expense?')) {
      try {
        await expenseAPI.deleteExpense(id);
        fetchExpenses();
      } catch (err) {
        console.error('Failed to delete expense:', err);
        alert('Failed to delete expense');
      }
    }
  };

  // Handle expense editing
  const handleEditExpense = (expense) => {
    setEditingExpense(expense);
    setShowForm(true);
  };

  // Handle viewing expense details
  const handleViewExpense = (expense) => {
    navigate(`/expense/${expense._id}`, { state: { expense } });
  };

  // Handle filter changes
  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  // Get category emoji
  const getCategoryEmoji = (category) => {
    const emojis = {
      'Food': 'ğŸ”',
      'Transportation': 'ğŸš—',
      'Entertainment': 'ğŸ¬',
      'Healthcare': 'ğŸ¥',
      'Shopping': 'ğŸ›ï¸',
      'Bills': 'ğŸ’¡',
      'Education': 'ğŸ“š',
      'Travel': 'âœˆï¸',
      'Other': 'ğŸ“'
    };
    return emojis[category] || 'ğŸ“';
  };

  // Format date
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  // Calculate totals
  const totalAmount = expenses.reduce((sum, expense) => sum + expense.amount, 0);
  const averageAmount = expenses.length > 0 ? totalAmount / expenses.length : 0;

  return (
    <div className="page-container">
      <div className="container slide-up">
        {/* Header */}
        <div className="flex justify-between items-center mb-lg">
          <div>
            <h1 className="text-4xl font-bold text-primary mb-sm">
              ğŸ“Š All Expenses
            </h1>
            <p className="text-secondary">
              View, search, and manage all your expenses in one place
            </p>
          </div>
          <div className="flex gap-md">
            <Link to="/dashboard" className="btn btn-outline">
              â† Back to Dashboard
            </Link>
            <button 
              className="btn btn-primary"
              onClick={() => {
                setEditingExpense(null);
                setShowForm(true);
              }}
            >
              â• Add Expense
            </button>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="message message-error">
            âš ï¸ {error}
          </div>
        )}

        {/* Quick Stats */}
        <div className="stats-grid">
          <div className="stats-card">
            <div className="stats-card-icon">ğŸ“ˆ</div>
            <div className="stats-card-title">Total Expenses</div>
            <div className="stats-card-value">{expenses.length}</div>
          </div>
          <div className="stats-card stats-highlight">
            <div className="stats-card-icon">ğŸ’°</div>
            <div className="stats-card-title">Total Amount</div>
            <div className="stats-card-value">{formatCurrency(totalAmount)}</div>
          </div>
          <div className="stats-card">
            <div className="stats-card-icon">ğŸ“Š</div>
            <div className="stats-card-title">Average</div>
            <div className="stats-card-value">{formatCurrency(averageAmount)}</div>
          </div>
        </div>

        {/* Search and Filters */}
        <div className="filters">
          <div className="card-header">
            <h3 className="card-title">Search & Filter</h3>
            <span className="demo-badge">
              ğŸ¯ Demo Feature
            </span>
          </div>
          
          {/* Search Bar */}
          <div className="form-group">
            <label className="form-label">ğŸ” Search Expenses</label>
            <input
              className="form-input"
              type="text"
              placeholder="Search by title, description, or category..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <div className="filters-grid">
            <div className="form-group">
              <label className="form-label">Category</label>
              <select
                className="form-select"
                value={filters.category}
                onChange={(e) => handleFilterChange({ ...filters, category: e.target.value })}
              >
                <option value="All">All Categories</option>
                <option value="Food">ğŸ” Food</option>
                <option value="Transportation">ğŸš— Transportation</option>
                <option value="Entertainment">ğŸ¬ Entertainment</option>
                <option value="Healthcare">ğŸ¥ Healthcare</option>
                <option value="Shopping">ğŸ›ï¸ Shopping</option>
                <option value="Bills">ğŸ’¡ Bills</option>
                <option value="Education">ğŸ“š Education</option>
                <option value="Travel">âœˆï¸ Travel</option>
                <option value="Other">ğŸ“ Other</option>
              </select>
            </div>
            
            <div className="form-group">
              <label className="form-label">Start Date</label>
              <input
                className="form-input"
                type="date"
                value={filters.startDate}
                onChange={(e) => handleFilterChange({ ...filters, startDate: e.target.value })}
              />
            </div>
            
            <div className="form-group">
              <label className="form-label">End Date</label>
              <input
                className="form-input"
                type="date"
                value={filters.endDate}
                onChange={(e) => handleFilterChange({ ...filters, endDate: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label className="form-label">Sort By</label>
              <select
                className="form-select"
                value={filters.sortBy}
                onChange={(e) => handleFilterChange({ ...filters, sortBy: e.target.value })}
              >
                <option value="date">Date</option>
                <option value="amount">Amount</option>
                <option value="title">Title</option>
                <option value="category">Category</option>
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Order</label>
              <select
                className="form-select"
                value={filters.sortOrder}
                onChange={(e) => handleFilterChange({ ...filters, sortOrder: e.target.value })}
              >
                <option value="desc">Descending</option>
                <option value="asc">Ascending</option>
              </select>
            </div>
            
            <button 
              className="btn btn-outline"
              onClick={() => {
                setFilters({ category: 'All', startDate: '', endDate: '', sortBy: 'date', sortOrder: 'desc' });
                setSearchTerm('');
              }}
            >
              Clear All
            </button>
          </div>
        </div>

        {/* Expense Form Modal */}
        {showForm && (
          <div className="modal-backdrop fade-in">
            <div className="modal slide-up">
              <ExpenseForm
                expense={editingExpense}
                onSubmit={handleExpenseSubmit}
                onCancel={() => {
                  setShowForm(false);
                  setEditingExpense(null);
                }}
              />
            </div>
          </div>
        )}

        {/* Expenses Grid */}
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Expense Results</h3>
            <p className="card-subtitle">
              Showing {expenses.length} expense{expenses.length !== 1 ? 's' : ''}
              {searchTerm && ` matching "${searchTerm}"`}
            </p>
          </div>

          {loading ? (
            <div className="text-center">
              <div className="spinner" style={{ margin: '0 auto' }}></div>
              <p className="text-secondary mt-md">Loading expenses...</p>
            </div>
          ) : expenses.length === 0 ? (
            <div className="text-center">
              <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>
                {searchTerm || filters.category !== 'All' || filters.startDate || filters.endDate ? 'ğŸ”' : 'ğŸ“Š'}
              </div>
              <h3 className="text-secondary mb-sm">
                {searchTerm || filters.category !== 'All' || filters.startDate || filters.endDate 
                  ? 'No expenses found' 
                  : 'No expenses yet'}
              </h3>
              <p className="text-muted">
                {searchTerm || filters.category !== 'All' || filters.startDate || filters.endDate
                  ? 'Try adjusting your search or filters to find more expenses.'
                  : 'Get started by adding your first expense.'}
              </p>
              <button 
                className="btn btn-primary mt-md"
                onClick={() => {
                  setEditingExpense(null);
                  setShowForm(true);
                }}
              >
                Add Your First Expense
              </button>
            </div>
          ) : (
            <div className="expenses-grid">
              {expenses.map((expense) => (
                <div 
                  key={expense._id} 
                  className={`expense-card category-${expense.category.toLowerCase()}`}
                  onClick={() => handleViewExpense(expense)}
                >
                  <div className="expense-card-header">
                    <h4 className="expense-card-title">{expense.title}</h4>
                    <div className="expense-card-amount">
                      {formatCurrency(expense.amount)}
                    </div>
                  </div>
                  
                  <div className="expense-card-meta">
                    <div className="expense-card-category">
                      {getCategoryEmoji(expense.category)}
                      {expense.category}
                    </div>
                    <div className="expense-card-date">
                      {formatDate(expense.date)}
                    </div>
                  </div>
                  
                  {expense.description && (
                    <div className="expense-card-description">
                      {expense.description}
                    </div>
                  )}
                  
                  <div className="expense-card-actions">
                    <button 
                      className="btn btn-sm btn-ghost"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleViewExpense(expense);
                      }}
                    >
                      ğŸ‘ï¸ View
                    </button>
                    <button 
                      className="btn btn-sm btn-outline"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleEditExpense(expense);
                      }}
                    >
                      âœï¸ Edit
                    </button>
                    <button 
                      className="btn btn-sm btn-danger"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteExpense(expense._id);
                      }}
                    >
                      ğŸ—‘ï¸ Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AllExpenses;
