import React from 'react';

const ExpenseStats = ({ stats }) => {
  const formatAmount = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getCategoryColor = (category) => {
    const colors = {
      Food: '#ff6b6b',
      Transportation: '#4ecdc4',
      Entertainment: '#45b7d1',
      Healthcare: '#f39c12',
      Shopping: '#e74c3c',
      Bills: '#9b59b6',
      Education: '#2ecc71',
      Travel: '#1abc9c',
      Other: '#95a5a6'
    };
    return colors[category] || '#95a5a6';
  };

  return (
    <div style={{ display: 'grid', gap: '25px', marginBottom: '35px' }}>
      {/* Summary Cards - 3 Column Grid Layout */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        {/* Total Amount Card */}
        <div className="bg-gradient-to-br from-blue-600 to-blue-800 rounded-2xl shadow-lg p-6 text-white transform hover:scale-105 transition-all duration-300">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-blue-100 text-sm font-medium uppercase tracking-wide mb-2">ÔøΩ Total Amount</p>
              <p className="text-4xl font-bold mb-1">{formatAmount(stats.totalAmount)}</p>
              <p className="text-blue-200 text-sm">Your total spending</p>
            </div>
            <div className="bg-white/20 rounded-full p-4">
              <svg className="w-8 h-8 text-white" fill="currentColor" viewBox="0 0 20 20">
                <path d="M4 4a2 2 0 00-2 2v1h16V6a2 2 0 00-2-2H4z"/>
                <path fillRule="evenodd" d="M18 9H2v5a2 2 0 002 2h12a2 2 0 002-2V9zM4 13a1 1 0 011-1h1a1 1 0 110 2H5a1 1 0 01-1-1zm5-1a1 1 0 100 2h1a1 1 0 100-2H9z" clipRule="evenodd"/>
              </svg>
            </div>
          </div>
        </div>

        {/* Total Expenses Card */}
        <div className="bg-gradient-to-br from-emerald-600 to-emerald-800 rounded-2xl shadow-lg p-8 text-white transform hover:scale-105 transition-all duration-300">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-emerald-100 text-sm font-medium uppercase tracking-wide mb-2">ÔøΩ Total Expenses</p>
              <p className="text-4xl font-bold mb-1">{stats.totalExpenses}</p>
              <p className="text-emerald-200 text-sm">Number of transactions</p>
            </div>
            <div className="bg-white/20 rounded-full p-4">
              <svg className="w-8 h-8 text-white" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M3 3a1 1 0 000 2v8a2 2 0 002 2h2.586l-1.293 1.293a1 1 0 101.414 1.414L10 15.414l2.293 2.293a1 1 0 001.414-1.414L12.414 15H15a2 2 0 002-2V5a1 1 0 100-2H3zm11.707 4.707a1 1 0 00-1.414-1.414L10 9.586 8.707 8.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd"/>
              </svg>
            </div>
          </div>
        </div>

        {/* Average per Expense Card */}
        <div className="bg-gradient-to-br from-purple-600 to-purple-800 rounded-2xl shadow-lg p-8 text-white transform hover:scale-105 transition-all duration-300">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-purple-100 text-sm font-medium uppercase tracking-wide mb-2">üìà Average per Expense</p>
              <p className="text-4xl font-bold mb-1">{formatAmount(stats.totalExpenses > 0 ? stats.totalAmount / stats.totalExpenses : 0)}</p>
              <p className="text-purple-200 text-sm">Average transaction value</p>
            </div>
            <div className="bg-white/20 rounded-full p-4">
              <svg className="w-8 h-8 text-white" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM6.293 6.707a1 1 0 010-1.414l3-3a1 1 0 011.414 0l3 3a1 1 0 01-1.414 1.414L11 5.414V13a1 1 0 11-2 0V5.414L7.707 6.707a1 1 0 01-1.414 0z" clipRule="evenodd"/>
              </svg>
            </div>
          </div>
        </div>
      </div>

      {/* Category Breakdown */}
      {stats.categoryStats && stats.categoryStats.length > 0 && (
        <div className="card">
          <h3 style={{ marginBottom: '25px', color: '#333', fontWeight: '700', fontSize: '20px' }}>
            üìä Spending by Category
          </h3>
          <div style={{ display: 'grid', gap: '15px' }}>
            {stats.categoryStats.map((category, index) => {
              const percentage = ((category.totalAmount / stats.totalAmount) * 100).toFixed(1);
              const categoryEmoji = {
                Food: 'üçî',
                Transportation: 'üöó',
                Entertainment: 'üé¨',
                Healthcare: 'üè•',
                Shopping: 'üõçÔ∏è',
                Bills: 'üí°',
                Education: 'üìö',
                Travel: '‚úàÔ∏è',
                Other: 'üìù'
              };
              
              return (
                <div key={index} style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'space-between',
                  padding: '18px 20px',
                  background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.1) 0%, rgba(255, 255, 255, 0.05) 100%)',
                  borderRadius: '12px',
                  border: `2px solid ${getCategoryColor(category._id)}`,
                  transition: 'all 0.3s ease',
                  cursor: 'pointer'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'translateX(5px)';
                  e.currentTarget.style.boxShadow = `0 5px 15px ${getCategoryColor(category._id)}33`;
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'translateX(0)';
                  e.currentTarget.style.boxShadow = 'none';
                }}
                >
                  <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
                    <div style={{ fontSize: '24px' }}>
                      {categoryEmoji[category._id] || 'üìù'}
                    </div>
                    <div>
                      <span style={{ fontWeight: '600', fontSize: '16px', color: '#333' }}>
                        {category._id}
                      </span>
                      <div style={{ color: '#666', fontSize: '13px', marginTop: '2px' }}>
                        {category.count} expense{category.count !== 1 ? 's' : ''}
                      </div>
                    </div>
                  </div>
                  
                  <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
                    <div style={{ 
                      background: `linear-gradient(90deg, ${getCategoryColor(category._id)} 0%, ${getCategoryColor(category._id)}88 100%)`,
                      height: '8px',
                      borderRadius: '4px',
                      width: `${Math.max(percentage * 3, 30)}px`,
                      boxShadow: `0 2px 8px ${getCategoryColor(category._id)}33`
                    }} />
                    <div style={{ textAlign: 'right' }}>
                      <div style={{ fontWeight: '700', fontSize: '16px', color: getCategoryColor(category._id) }}>
                        {formatAmount(category.totalAmount)}
                      </div>
                      <div style={{ color: '#666', fontSize: '12px', fontWeight: '500' }}>
                        {percentage}%
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default ExpenseStats;
