import React from 'react';
import { useTheme } from '../context/ThemeContext';

const TrendChart = ({ data }) => {
  const { isDarkMode } = useTheme();

  if (!data || data.length === 0) {
    return (
      <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md p-6`}>
        <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>No data available</p>
      </div>
    );
  }

  const maxAmount = Math.max(...data.map(d => d.total));
  const minAmount = Math.min(...data.map(d => d.total));
  const range = maxAmount - minAmount || 1;

  return (
    <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md p-6`}>
      <h3 className={`text-xl font-semibold ${isDarkMode ? 'text-white' : 'text-gray-800'} mb-6`}>
        ðŸ“Š 12-Month Spending Trend
      </h3>
      
      <div className="overflow-x-auto">
        <div className="flex gap-2 pb-4" style={{ minWidth: '100%' }}>
          {data.map((item, index) => {
            const height = ((item.total - minAmount) / range) * 200 + 20;
            return (
              <div key={index} className="flex-1 flex flex-col items-center">
                <div className="relative w-full h-64 flex items-flex-end justify-center mb-2">
                  <div
                    className={`${
                      isDarkMode
                        ? 'bg-gradient-to-t from-primary-600 to-primary-400'
                        : 'bg-gradient-to-t from-primary-500 to-primary-300'
                    } rounded-t-lg transition-all duration-300 hover:shadow-lg`}
                    style={{
                      height: `${height}px`,
                      minHeight: '20px',
                      width: '100%'
                    }}
                    title={`${item.month}: $${item.total.toFixed(2)}`}
                  />
                </div>
                <div className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-600'} font-medium`}>
                  {item.month.split('-')[1]}/{item.month.split('-')[0].slice(-2)}
                </div>
                <div className={`text-xs ${isDarkMode ? 'text-gray-500' : 'text-gray-500'}`}>
                  ${item.total.toFixed(0)}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default TrendChart;
