import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [formErrors, setFormErrors] = useState({});

  const { login, loading, error, isAuthenticated, clearError } = useAuth();
  const navigate = useNavigate();

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/dashboard');
    }
  }, [isAuthenticated, navigate]);

  // Clear errors when component mounts
  useEffect(() => {
    clearError();
  }, [clearError]);

  const { email, password } = formData;

  const onChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    // Clear field error when user starts typing
    if (formErrors[e.target.name]) {
      setFormErrors({
        ...formErrors,
        [e.target.name]: ''
      });
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!email.trim()) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      errors.email = 'Email is invalid';
    }

    if (!password) {
      errors.password = 'Password is required';
    } else if (password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    return errors;
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    const result = await login(formData);
    if (result.success) {
      navigate('/dashboard');
    }
  };

  return (
    <div className="container">
      <form onSubmit={onSubmit} className="auth-form card">
        <h2>üîê Welcome Back</h2>
        <p style={{ textAlign: 'center', color: '#666', marginBottom: '30px' }}>
          Sign in to continue managing your expenses
        </p>
        
        {error && (
          <div style={{ 
            background: 'rgba(220, 53, 69, 0.1)', 
            border: '2px solid #dc3545', 
            borderRadius: '12px',
            padding: '15px',
            marginBottom: '20px',
            textAlign: 'center'
          }}>
            <div className="error" style={{ fontSize: '16px' }}>{error}</div>
          </div>
        )}
        
        <div className="form-group">
          <label htmlFor="email">üìß Email Address</label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onChange={onChange}
            placeholder="Enter your email address"
          />
          {formErrors.email && <div className="error">{formErrors.email}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="password">üîí Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={onChange}
            placeholder="Enter your password"
          />
          {formErrors.password && <div className="error">{formErrors.password}</div>}
        </div>

        <button 
          type="submit" 
          className="btn btn-primary" 
          style={{ width: '100%', fontSize: '16px', fontWeight: '600' }}
          disabled={loading}
        >
          {loading ? (
            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
              <div className="loading"></div>
              Signing In...
            </span>
          ) : (
            'üöÄ Sign In'
          )}
        </button>

        <p className="text-center mt-3" style={{ color: 'white' }}>
          Don't have an account? {' '}
          <Link 
            to="/register" 
            style={{ 
              color: '#51cf66', 
              textDecoration: 'none', 
              fontWeight: '600',
              borderBottom: '2px solid transparent',
              transition: 'border-color 0.3s ease'
            }}
            onMouseEnter={(e) => e.target.style.borderBottomColor = '#51cf66'}
            onMouseLeave={(e) => e.target.style.borderBottomColor = 'transparent'}
          >
            Sign up here
          </Link>
        </p>
      </form>
    </div>
  );
};

export default Login;
